SELECT sum("value")/10 FROM (
SELECT mean("count") AS "value" FROM "loadbalancer.app.requests" WHERE ("application" =~ /^$app$/ AND "gcp_project" != 'tgt-cnf-lab'  AND "cluster" =~ /^$cluster$/ AND lb_type = 'envoy' AND "environment" =~ /^$env$/) AND $timeFilter GROUP BY time($__interval), "host", "cluster", "server_group", "region"
) WHERE  $timeFilter GROUP BY time($__interval), "server_group" fill(null)

SELECT mean("total_weighted_rt") / mean("total_requests") AS "avg_response_time" FROM (
 SELECT sum("requests") AS "total_requests", sum("weighted_rt") AS "total_weighted_rt" FROM (
 SELECT $__interval_ms * mean("rate.1m") AS "requests", $__interval_ms * mean("rate.1m") * mean("latency.avg") AS "weighted_rt" FROM "loadbalancer.app.requests" 
WHERE ("application" =~ /^$app$/ AND "cluster" =~ /^$cluster$/ AND "environment" =~ /^$env$/ AND lb_type != 'envoy') AND $timeFilter GROUP BY time($interval), "host", "cluster"
  ) WHERE $timeFilter GROUP BY  time($interval), "cluster"
) WHERE $timeFilter GROUP BY  time($interval), "cluster"

